import React, {useState, useEffect, useReducer} from 'react';
import axios from "axios";

import SpeakerSearchBar from "../SpeakerSearchBar/SpeakerSearchBar";
import Speaker from "../Speaker/Speaker";

const REQUEST_STATUS = {
    LOADING: "loading",
    SUCCESS: "success",
    ERROR: "error"
};

const Speakers = () => {

    const [searchQuery, setSearchQuery] = useState("");
    const reducer = (state, action) => {
        switch (action.type) {
            case "GET_ALL_SUCCESS":
                return {
                    ...state,
                    speakers: action.speakers
                };
            case "UPDATE_STATUS":
                return {
                    ...state,
                    status: action.status
                }

        }
    }
    const [{speakers, status}, setSpeakers] = useReducer(reducer,[]);
    const [error, setError] = useState({});


    useEffect(() => {
        const fetchSpeakers = async () => {
            try {
                const response = await axios.get("http://localhost:4000/speakers");
                setSpeakers({
                    data: response.data,
                    type: "GET_ALL_SUCCESS"
                });
                setStatus({
                    status: REQUEST_STATUS.SUCCESS,
                    type: "UPDATE_SUCCESS"
                });
            } catch (e) {
                setStatus(REQUEST_STATUS.ERROR);
                setError(e);
            }
        }
        fetchSpeakers();
    }, []);

    async function onFavoriteToggleHandler(speakerRec) {
        const toggledSpeakerRec = toggleSpeakerFavorite(speakerRec);
        const speakerIndex = speakers.map((speaker) => speaker.id).indexOf(speakerRec.id);
        try {
            await axios.put(`http://localhost:4000/speakers/${speakerRec.id}`, toggledSpeakerRec);
            setSpeakers(
                [
                    ...speakers.slice(0, speakerIndex),
                    toggledSpeakerRec,
                    ...speakers.slice(speakerIndex + 1)
                ]
            );
        } catch (e) {
            setStatus(REQUEST_STATUS.ERROR);
            setError(e);
        }
    }

    function toggleSpeakerFavorite(speakerRec) {
        return {
            ...speakerRec,
            isFavorite: !speakerRec.isFavorite,
        };
    }

    const success = status === REQUEST_STATUS.SUCCESS;
    const isLoading = status === REQUEST_STATUS.LOADING;
    const hasErrored = status === REQUEST_STATUS.ERROR;

    return (
        <div>
            <SpeakerSearchBar
                searchQuery={searchQuery}
                setSearchQuery={setSearchQuery}
            />
            {isLoading && <div>Loading...</div>}
            {hasErrored && <div>
                Loading error... Is the json-server running? (try "npm run
                json-server" at terminal prompt)
                <br/>
                <b>ERROR: {error.message}</b>
            </div>}
            {success && (
                <div className="grid md:grid-cols-2 lg:grid-cols-3 grid-cols-1 gap-12">
                    {speakers.filter((rec) => {
                        const targetString = `${rec.firstName} ${rec.lastName}`.toLowerCase();
                        return searchQuery.length === 0 ? true : targetString.includes(searchQuery.toLowerCase());
                    })
                        .map((speaker) => (
                            <Speaker
                                key={speaker.id}
                                {...speaker}
                                onFavoriteToggle={() => onFavoriteToggleHandler(speaker)}
                            />
                        ))
                    }
                </div>
            )}
        </div>
    );
};
export default Speakers;
