import React, {useState, useEffect, useReducer} from 'react';
import axios from "axios";

import SpeakerSearchBar from "../SpeakerSearchBar/SpeakerSearchBar";
import Speaker from "../Speaker/Speaker";
import {GET_ALL_FAILURE, GET_ALL_SUCCESS, PUT_FAILURE, PUT_SUCCESS} from "../../actions/request";
import requestReducer, {REQUEST_STATUS} from "../../reducers/request";
import withRequest from "../HOC/withRequest";

const Speakers = ({ records: speakers, status, error, put}) => {

    const [searchQuery, setSearchQuery] = useState("");





    async function onFavoriteToggleHandler(speakerRec) {

    }

    const success = status === REQUEST_STATUS.SUCCESS;
    const isLoading = status === REQUEST_STATUS.LOADING;
    const hasErrored = status === REQUEST_STATUS.ERROR;

    return (
        <div>
            <SpeakerSearchBar
                searchQuery={searchQuery}
                setSearchQuery={setSearchQuery}
            />
            {isLoading && <div>Loading...</div>}
            {hasErrored && <div>
                Loading error... Is the json-server running? (try "npm run
                json-server" at terminal prompt)
                <br/>
                <b>ERROR: {error.message}</b>
            </div>}
            {success && (
                <div className="grid md:grid-cols-2 lg:grid-cols-3 grid-cols-1 gap-12">
                    {speakers.filter((rec) => {
                        const targetString = `${rec.firstName} ${rec.lastName}`.toLowerCase();
                        return searchQuery.length === 0 ? true : targetString.includes(searchQuery.toLowerCase());
                    })
                        .map((speaker) => (
                            <Speaker
                                key={speaker.id}
                                {...speaker}
                                onFavoriteToggle={() => onFavoriteToggleHandler(speaker)}
                            />
                        ))
                    }
                </div>
            )}
        </div>
    );
};
export default withRequest()(Speakers);
